import { useState } from "react";
import "../Characters/styles.css";
export const CharCard = ({ character }) => {
  const [nameCharacter, setNameCharacter] = useState("");

  const countNameCharacter = (name) => {
    if (name.length > 15) {
      setNameCharacter(name.slice(0, 15));
    }
    return name.length;
  };
  return (
    <>
      <div className="cards">
        <p>{character.name} </p>
      </div>
    </>
  );
};
