import { useState } from "react";
import "../Characters/styles.css";
export const CharCard = ({ character, key }) => {
  const handleName = (name) => {
    let handledName = name.split(" ").join("");
    if (handledName.length > 15) {
      return name.slice(0, 13) + "...";
    }
    return name;
  };

  const handleStatus = (status) => {
    if (status === "Alive") {
      return true;
    }
    return false;
  };

  const { name, image, status, species } = character;
  return (
    <>
      <div key={key} className="cards">
        {handleStatus(status) ? (
          <div style={{ color: "blue", backgroundColor: "lightblue" }}>
            {" "}
            <p>Name: {handleName(name)}</p>
            <p> Specie: {species}</p>
            <img alt="" src={image}></img>
          </div>
        ) : (
          <div style={{ color: "red", backgroundColor: "pink" }}>
            <p>{handleName(name)}</p>
            <p> Specie: {species}</p>
            <img alt="" src={image}></img>
          </div>
        )}
      </div>
    </>
  );
};
