import { useState } from "react";
import "../Characters/styles.css";
export const CharCard = ({ character }) => {
  const [carac, setCarac] = useState("");

  const handleName = (name) => {
    if (name.length > 15) {
      return name.slice(0, 16) + "...";
    }
    return name;
  };

  return (
    <>
      <div className="cards">
        <p>{handleName(character.name)}</p>
        <img alt="" src={character.image} width="100" height="50"></img>
      </div>
    </>
  );
};
