import "../Characters/styles.css";
import Alive from "../Alive";
import NotAlive from "../NotAlive";

const CharCard = ({ character, key }) => {
  const { name, image, status, species } = character;

  const handleName = (name) => {
    let handledName = name.split(" ").join("");
    if (handledName.length > 15) {
      return name.slice(0, 13) + "...";
    }
    return name;
  };

  return (
    <>
      <div key={key} className="cards">
        {status === "Alive" ? (
          <Alive
            name={name}
            species={species}
            image={image}
            handleName={handleName}
            status={status}
          />
        ) : (
          <NotAlive
            name={name}
            species={species}
            image={image}
            handleName={handleName}
          />
        )}
      </div>
    </>
  );
};

export default CharCard;
