import { useState } from "react";
import "../Characters/styles.css";
export const CharCard = ({ character }) => {
  const handleName = (name) => {
    let handledName = name.split(" ").join("");
    if (handledName.length > 15) {
      return name.slice(0, 13) + "...";
    }
    return name;
  };

  const handleStatus = (status) => {
    if (status === "Alive") {
      return true;
    }
    return false;
  };

  return (
    <>
      <div className="cards">
        {handleStatus(CharCard.status) ? (
          <p style={{ color: "red" }}>Nicole ok</p>
        ) : (
          <p>Nicole nok </p>
        )}

        <img alt="" src={character.image}></img>
      </div>
    </>
  );
};
